mapper.select:
SELECT
		count(*) FROM `shoes_4jo`.`member` WHERE member_id =#{member_id} AND
		member_pw = #{member_pw}

mapper.update:
update `shoes_4jo`.`member`
set member_name=#{member_name}, member_pw=#{member_pw}
, member_email=#{member_email},member_phone=#{member_phone}
where
member_id=#{member_id}

